		if(path.indexOf("/") == -1){
			path = dojo._getModuleSymbols(resourceName).join("/") + '.css';
			path = ((path.charAt(0) == '/' || path.match(/^\w+:/)) ? "" : dojo.baseUrl) + path;
		}
		if(dojo.config.cacheBust){
			path += (path.indexOf("?") == -1 ? "?" : "&") + String(dojo.config.cacheBust).replace(/\W+/g,"");
		}

