dojo.declare(
	"spring.UrlBuilder",
	null,
{
	// summary:
	//		Generate a URL provided a template and parameter data

	// template: String
	//		URL template
	template: null,

	// params: Object
	//		the parameters to apply to the template
	params: null,	

	
	constructor: function(/*String*/template, /*Object*/params){
		// summary:
		//		Set up properties and initialize chart build.
		//
		//	arguments:
		//		template: String
		//			URL template
		//
		//		params: Object
		//			default URL params
		
		this.template = template;
		this.params = params;
	},
	
	append: function(/*String*/template, /*Object*/params){
		// summary:
		//		Create a new UrlBuilder instance that extends the current builder.
		//		The template value is appended to the current builders template.
		//		Params are mixed in.
		//
		//	arguments:
		//		template: String
		//			URL template
		//
		//		params: Object
		//			default URL params
		
		return new spring.UrlBuilder(this.template + template, dojo.delegate(this.params, params));
	},
	
	build: function(/*Object*/params){
		// summary:
		//		Return the expanded URL for the parameters
		//
		//	arguments:
		//		params: Object
		//			parameters to apply to the URL template.  These parameters are 
		//			combined with and override the default parameters 
		
		return this._buildUrl(this.template, dojo.delegate(this.params, params));
	},
	
	toString: function(){
		// summary:
		//		Return a String representation of the URL by invoking the build method 
		//		with no params.
		
		return this.build();
	},
	
	_buildUrl: function(/*String*/template, /*Object*/params){
		// summary:
		//		Internal builder to convert template with params.  This function is 
		//		intentionally only using core JS calls to maintain toolkit portability.
		
		var url = template,
			name = null,
			queryStringParams = {},
			re = null,
			matched = null;
		if(params){
			for(name in params){
				re = new RegExp("\\{" + name + "\\}");
				matched = false;
				if(re.test(url)){
					matched = true;
					url = url.replace(re, encodeURIComponent(params[name]), "g");
				}
				if(!matched){
					queryStringParams[name] = params[name];
				}
			}
			for(name in queryStringParams){
				if(queryStringParams[name]){
					url += url.indexOf("?") === -1 ? "?" : "&";
					url += encodeURIComponent(name);
					url += "=";
					url += encodeURIComponent(queryStringParams[name]);
				}
			}
		}
		return url;
	}
				
});

