dojo.declare("insight.traces.FrameStack", null, {
	
	// autoOpenThreshold: Number
	//		Percent (0.0-1.0) of parent's duration that indicates the frame 
	//		should be pre-expanded.
	autoOpenThreshold: 0.1,
	
	// traceId: String [required]
	//		The identifier for the trace's frame stack that is loaded
	traceId: null,
	
	// tree: dijit.Tree
	//		The tree instance created
	tree: null,
	
	timeout: insight.runtime.getXhrTimeout("FrameStack"),

	constructor: function(params, node) {
		this.autoOpenThreshold = params.autoOpenThreshold || this.autoOpenThreshold;
		this.traceId = params.traceId;
		if (!this.traceId) {
			throw("traceId is required to create FrameStack");
		}
		if (dojo.isIE < 9) {
			this.autoOpenThreshold *= 2;
		}
		dojo.xhrGet({
			url: params.traceUri.build(),
			handleAs: "json",
			timeout: this.timeout,
			load: dojo.hitch(this, function(response, ioArgs) {
				this._build(node, response, params.operationUri);
			}),
			error: function() {
				dojo.publish("error/xhr", arguments);
			}
		});
	},
	
	_build: function(frameStackRoot, traceData, operationUri) {
		var treeStore = this._buildTreeStore(traceData.trace, operationUri);
		this.tree = new dijit.Tree({
			onClick: dojo.hitch(this, this.toggleOperation),
			model: treeStore,
			persist: false,
			_createTreeNode: function(args) {
				args.duration = dojo.number.format(this.model.store.getValue(args.item, "duration")) + " ms";
				if(this.model.store.hasAttribute(args.item, "durationSelf")){
					args.ganttTooltip = dojo.number.format(this.model.store.getValue(args.item, "durationSelf")) + " ms (self time)";
					args.selfFragments = this.model.store.getValue(args.item, "selfFragments");
				}
				args.traceStartNanos = this.model.store.getValue(args.item, "traceStartNanos");
				args.traceEndNanos = this.model.store.getValue(args.item, "traceEndNanos");
				args.frameStartNanos = this.model.store.getValue(args.item, "frameStartNanos");
				args.frameEndNanos = this.model.store.getValue(args.item, "frameEndNanos");
				return new insight.traces.FrameTreeNode(args);
			}
		}, frameStackRoot);
		this.tree.startup();
		this._expandTree(this.tree);
	},

	_buildTreeStore: function(trace, operationUri) {
		var frames = {
			identifier: "id",
			label: "label",
			traceId: trace.id,
			range: trace.range,
			items: []
		};
		this._processFrame(frames, trace.frameStack, null, operationUri);
		return new dijit.tree.TreeStoreModel({
			store: new dojo.data.ItemFileReadStore({data: frames}),
			query: {type:"root"}
		});
	},
	
	_processFrame: function(parsedFrames, frame, parentItem, operationUri) {
		var item = {
			id: frame.id,
			traceId: parsedFrames.traceId,
			frameId: frame.id,
			url: operationUri.build({ traceId: parsedFrames.traceId, frameId: frame.id }),
			label: frame.operation.label,
			duration: frame.range.duration,
			durationSelf: frame.range.selfDuration,
			traceStartNanos: parsedFrames.range.startNanos,
			traceEndNanos: parsedFrames.range.endNanos,
			frameStartNanos: frame.range.startNanos,
			frameEndNanos: frame.range.endNanos,
			selfFragments: dojo.toJson(frame.range.selfFragments)
		};

		if (parentItem) {
			if (!parentItem.children) {
				parentItem.children = [];
			}
			parentItem.children.push(item);
			item.parentId = parentItem.frameId;
		} else {
			item.type = "root";
			parsedFrames.items.push(item);
		}
		
		dojo.forEach(frame.children, function(childFrame){
			this._processFrame(parsedFrames, childFrame, item, operationUri);
		}, this);
	},

	_expandTree: function(tree) {
		var operationsToOpen = this._openedOperations();
		this._expandTreeNode(tree, tree.rootNode, operationsToOpen);
		if (dojo.indexOf(operationsToOpen, tree.rootNode.item.frameId) != -1) {
			this._toggleOperation(tree.rootNode.item, tree.rootNode);
		}
	},

	_expandTreeNode: function(tree, node, operationsToOpen) {
		tree._expandNode(node);
		dojo.forEach(node.getChildren(), function(childNode) {
			var percentOfParent = childNode.item.duration / node.item.duration;
			if (percentOfParent > this.autoOpenThreshold || this._forceExpand(childNode.item, operationsToOpen)) {
				this._expandTreeNode(tree, childNode, operationsToOpen);
			}
			if (dojo.indexOf(operationsToOpen, childNode.item.frameId) != -1) {
				this._toggleOperation(childNode.item, childNode);
			}
		}, this);
		node.setSelected(false);
	},
	
	_forceExpand: function(item, operationsToOpen) {
		if (item.forceExpand === false) {
			return false;
		}
		if (dojo.indexOf(operationsToOpen, item.frameId) != -1) {
			return true;
		}
		if (item.children) {
			for (var i=0; i<item.children.length; i++) {
				if (this._forceExpand(item.children[i], operationsToOpen)) {
					return true;
				}
			}
		}
		item.forceExpand = false;
		return false;
	},

	toggleOperation: function(item, node) {
		var opened = this._toggleOperation(item, node);
		if (dojo.cookie.isSupported()) {
			this._updateCookie(item.frameId, !!opened);
		}
	},
	
	_openedOperations: function() {
		var cookieVal = dojo.cookie(this.tree.id + "-operations");
		if (!cookieVal) {
			return [];
		}
		var trace = cookieVal.split(":")[0];
		if (trace != this.traceId) {
			return [];
		}
		return cookieVal.split(":")[1].split(",");
	},
	
	_updateCookie: function(frameId, opened) {
		var operations = this._openedOperations();
		if (opened) {
			operations.push(frameId);
		}
		else {
			operations = dojo.filter(operations, function(i) { return i != frameId }, this);
		}
		dojo.cookie(this.tree.id + "-operations", this.traceId + ":" + operations.join(","));
	},
	
	_toggleOperation: function(item, node) {
		var treeRow = dojo.query("> .dijitTreeRow", node.domNode)[0];
		if (node.operation) {
			node.operation.destroyRecursive();
			dojo.removeClass(treeRow, "dijitTreeNodeOpen");
			node.operation = null;
		}
		else {
			var cancelBubble = function(event) {
				// prevent click and dblClick events from inside the operation UI from bubbling up and closing the operation
				event.cancelBubble = true;
			}
			node.operation = new dijit.layout.ContentPane({
				href: item.url,
				id: node.id + "_operation",
				"class": "operation",
				onClick: cancelBubble,
				onDblClick: cancelBubble
			});
			dojo.addClass(node.operation.domNode, "operation");
			dojo.addClass(treeRow, "dijitTreeNodeOpen");
			node.operation.placeAt(dojo.query("> .dijitTreeRow > .dijitTreeContent", node.domNode)[0], "last");
			node.operation.startup();
		}
		return node.operation;
	}

});

